              - collapseNames(tmp[,,"Emi|CO2|Carbon Capture and Storage|Biomass|Energy|Demand|Industry (Mt CO2/yr)"]/
                                GtC_2_MtCO2)
                
        )
        * p_share_carbonCapture_stor
        )
        + CDRco2luc[,,"co2luc"]
      ) 
      * GtC_2_MtCO2,
